starter_code:
def sum_range(n: int) -> int:
    s = 0
    for i in range(1, n):
        s += i
    return s

tests:
def test_sum_range():
    assert sum_range(1)==1
    assert sum_range(5)==15

starter_code:
def factorial(n: int) -> int:
    if n == 0:
        return 0
    out = 1
    for i in range(1, n+1):
        out *= i
    return out

tests:
def test_factorial():
    assert factorial(0)==1
    assert factorial(5)==120

