starter_code:
def second_largest(nums: list[int]) -> int:
    nums = sorted(nums)
    return nums[-2]

tests:
def test_second_largest():
    assert second_largest([1,2,3])==2
    assert second_largest([5,5,4,4,3])==4

starter_code:
def is_palindrome(s: str) -> bool:
    s = s.replace(' ', '')
    return s == s[::-1]

tests:
def test_is_palindrome():
    assert is_palindrome('Race car') is True
    assert is_palindrome('hello') is False

def test_has_duplicates():
    assert has_duplicates([1,2,2]) is True
    assert has_duplicates([1,2,3]) is False

starter_code:
def gcd(a: int, b: int) -> int:
    while a != b:
        if a > b:
            a = a - b
        else:
            b = b - a
    return a

tests:
def test_gcd():
    assert gcd(54,24)==6
    assert gcd(1,99991)==1

def test_absmax():
    assert absmax([-10, 3, 4])==-10
    assert absmax([1,-2,2])==-2
